import React, { Component } from 'react'
class Board extends Component{
    state={
        tasklist:'',
        stage:''
    }
    HandleChange(event)
      {
          this.setState({tasklist:event.target.value})
      }
      HandleStage(event)
      {
          this.setState({stage:event.target.value})
      }
     addTask=()=>
        {
            let task1=this.props.task
             let TaskData=JSON.parse(localStorage.getItem(this.props.username))
            console.log(this.props.task)
            console.log( TaskData.boards[task1][0].Todo)

            TaskData.boards[task1][0].todo.push(this.state.tasklist)
            console.log(TaskData)
             localStorage.setItem(this.props.username,JSON.stringify( TaskData))
            // console.log(this.props.task)
            // console.log(TaskData.boards[task1][0].todo)
        }
    render(){
       let data=JSON.parse(localStorage.getItem(this.props.username))
       let tasks=data.boards[this.props.task].map(item=>{
       return <th scope="col">{Object.keys(item)}</th>
       })
        return(
            <div>
<input class="form-control form-control-lg" id="ip1" type="text" placeholder="Add stage" onChange={(event)=>this.HandleStage(event)}/>
<button onClick={this.addTask}>Add</button>
<input class="form-control form-control-lg" id="ip1" type="text" placeholder="Eter task " onChange={(event)=>this.HandleChange(event)}/><br></br>
<button onClick={this.addTask}>Add</button>
                <table class="table table-striped table-responsive-md btn-table">
                    <thead>
                        {tasks}
                    </thead>
                    {/* <tbody>
                        <tr>
                            <td>todo1</td>

                        </tr>
                    </tbody> */}
                </table>

            </div>
        )
    }
}
export default Board;
