import React, { Component } from 'react';
import {Form, Button,Label,Input} from 'reactstrap';
import {Link} from 'react-router-dom';
import Board from '../Board/Board'
export default class BoardGroup extends Component {
    constructor(props) {
        super(props);
        this.state={
            username:this.props.username,
            password:this.props.password,
            boardName:null,
        }
    }
    handleNewBoard=(e)=>{
        e.preventDefault();
        if(e.target.value===""||e.target.value===null)
        {
            alert("enter a correct name");
            return;
        }
        this.setState({boardName:e.target.value});
    }
    handleSubmit=()=>{
        console.log(this.state)
        let boardData=JSON.parse(localStorage.getItem(this.props.username));
        let obj={
            username:this.state.username,
            password:this.state.password,
            boards:{
                ...boardData.boards,
                [this.state.boardName]:{},
            }
        }
        localStorage.setItem(this.state.username,JSON.stringify(obj))        
    }
    render(){
        let boardList=JSON.parse(localStorage.getItem(this.props.username));
        // if(boardList){
        //     Object.keys(boardList.boards).map((key)=>{
        //         return(
        //             <li>{key}</li>
        //         )
        //     })
        // }
        return(
            <div>
            <Form>
                <Label >Name</Label>
                <Input type="text" onChange={this.handleNewBoard}></Input>
                <Button onClick={this.handleSubmit}> submit</Button>
            </Form>
            {
            Object.keys(boardList.boards).map((key)=>{
                return(
                    <Link to="/oard">{key}</Link>
                )
                })
        }
            </div>

        );
    }
}
