import React, { Component } from 'react';
import {Form, Button,Label,Input} from 'reactstrap';
import {Link,Switch,Route} from 'react-router-dom';
import Board from '../Board/Board';
export default class BoardGroup extends Component {
    constructor(props) {
        super(props);
        this.state={
            username:this.props.username,
            password:this.props.password,
            boardName:null,
            boardData:null

        }
    }
    handleNewBoard=(e)=>{
        e.preventDefault();
        if(e.target.value===""||e.target.value===null)
        {
            alert("enter a correct name");
            return;
        }
        this.setState({boardName:e.target.value});
    }
    handleSubmit=()=>{
        console.log(this.state)
        let boardData=JSON.parse(localStorage.getItem(this.props.username));
        let board = boardData.boards
        board = { ...board, [this.state.boardName]: [{ new: [] }, { done: [] }] }
        boardData.boards = board

        this.setState({ boardData: boardData })
        localStorage.setItem(this.state.username,JSON.stringify(boardData))        
    }
    tas
    render(){
        let boardList=JSON.parse(localStorage.getItem(this.props.username));
        if(boardList){
            Object.keys(boardList.boards).map((key)=>{
                return(
                    <li>{key}</li>
                )
            })
        }
        return(
            <div>
            <div class="md-form">

                <input class="form-control form-control-lg" type="text" placeholder="Enter Board " onChange={this.handleNewBoard}/>
                <button class="btn btn-outline-primary btn-sm m-0 waves-effect" onClick={this.handleSubmit}> submit</button>
            </div>
            {
            Object.keys(boardList.boards).map((key)=>{
                return(

                   <li> <Link to="/oard" onClick={this.taskProps}>{key}</Link></li>
                )
                })
        }
        <div>
                <Switch>
                    <Route path="/oard">
                        <Board username={this.state.username} task={this.state.boardName}/>
                    </Route>

                </Switch>
                </div>
            </div>

        );
    }
}
