import React from 'react'
import { Component } from 'react';
class Signup extends Component
{
    log=false
    com=true
    constructor(props) {
        super(props);
        this.state={
            username:'',
            password:'',
            // today:[{
            //     date:[],tasks:[],endTime:[],dur:[],
            // startTime:[]}],
            submit:false
        }
        this.HandleChange = this.HandleChange.bind(this);
        this.HandlePassword = this.HandlePassword.bind(this);
        this.HandleSubmit = this.HandleSubmit.bind(this);
        this.HandleLog=this.HandleLog.bind(this);
      } 
      render()
      {
    return(
        <div>
            {this.state.submit?
            <div className="SignUp">
             <SideBar username={this.state.username} onLogout={this.handleLogout}/>
             </div>:
            <div>
            <h1 className={this.log?"hide":"show"}>SignIn</h1>
            {/* <div className='show'> <button className={this.log?"show":"hide"} onClick={()=>{this.log=!this.log;this.setState({username:this.state.username,submit:false})}}>Signout</button></div> */}
             <div className={this.log?"hide":"show"}>
             <div class="md-form">
                {/* <i class="fas fa-user prefix"></i> */}
            <input class="form-control form-control-lg" id="ip1" type="text" placeholder="username" onChange={(event)=>this.HandleChange(event)}/><br></br>
            </div>
            <div class="md-form">
                {/* <i class="fas fa-user prefix"></i> */}
            <input class="form-control form-control-lg" id="ip2" type="password" placeholder="password" onChange={(event)=>this.HandlePassword(event)}/><br></br>
            <button class="btn btn-outline-primary btn-sm m-0 waves-effect" onClick={this.HandleSubmit}>signup</button>
            <button class="btn btn-outline-primary btn-sm m-0 waves-effect" onClick={this.HandleLog}>SignIn</button>
            </div>
            </div>
            </div>}
        </div>



    )
            }
}
export default Signup;
